mapper.insert:
insert into province set p_code=#{p_code}, p_name=#{p_name}, 
p_latitude=#{p_latitude}, p_longitude=#{p_longitude}
on duplicate key update p_code=#{p_code}, p_name=#{p_name}, 
p_latitude=#{p_latitude}, p_longitude=#{p_longitude}

insert into sigungu set p_code=#{p_code}, s_code=#{s_code}, s_name=#{s_name}, 
s_latitude=#{s_latitude}, s_longitude=#{s_longitude}
on duplicate key update p_code=${p_code}, s_code=#{s_code}, s_name=#{s_name}, 
s_latitude=#{s_latitude}, s_longitude=#{s_longitude}
